mapper.update:
UPDATE ANSWERBOARD
	SET CONTENT=#{content}, DELFLAG='N'
	WHERE SEQ=#{seq}

UPDATE ANSWERBOARD
	SET CONTENT=#{content}, DELFLAG='N'
	WHERE SEQ=#{seq}

mapper.delete:
UPDATE ANSWERBOARD 
	 SET DELFLAG ='Y'
	WHERE SEQ = #{seq}

UPDATE ANSWERBOARD
	SET DELFLAG='Y'
	WHERE SEQ=#{seq} AND EMAIL=#{email} AND PASSWORD =#{password}

mapper.select:
 SELECT COUNT(*)
	 	FROM ANSWERBOARD a 
	 	WHERE EMAIL=#{email} AND PASSWORD= #{password} AND SEQ=#{seq}
