mapper.update:
UPDATE PETINFO
	SET NAME=#{name}, BIRTH=#{birth}, WEIGHT=#{weight}, 
	        GENDER=#{gender}, "TYPE"=#{type}, TYPEINFO=#{typeinfo}, 
	        NOTIFICATION=#{notification}, PROFILE=#{profile}
	WHERE USER_EMAIL =#{user_email}
	AND NAME =#{name}

UPDATE PETINFO
	SET DELFLAG='Y'
	WHERE USER_EMAIL =#{user_email}
	AND NAME =#{name}
