mapper.select:
SELECT NAME
	FROM PETINFO p 
	WHERE USER_EMAIL =#{user_email}

	 SELECT (SELECT CODENAME FROM TYPECODE t  WHERE CODEID =RESERVETYPE) AS RESERVETYPE, TO_CHAR(r.RESERVEDATE,'YYYY-MM-DD')  AS RESERVEDATE, r.RESERVETIME, r.PET_NAME, r.USER_EMAIL,r.STATUS,u.PHONE, u.NAME, r.SEQ
         FROM RESERVATION r JOIN USERINFO u
         ON r.USER_EMAIL = u.EMAIL 
         AND r.HOSPITAL_SEQ = #{hospital_seq}
         AND r.SEQ =#{seq}

mapper.update:
	UPDATE RESERVATION 
        SET RESERVEDATE = #{reservedate}, RESERVETIME =#{reservetime}
        WHERE SEQ=${seq} AND STATUS='S' AND HOSPITAL_SEQ=#{hospital_seq} AND PAYNUM IS NOT NULL

		UPDATE RESERVATION
        SET STATUS='R', COMMNET =#{commnet}
        WHERE SEQ = #{seq} AND PAYNUM IS NOT NULL AND STATUS='S'

	UPDATE RESERVATION 
        SET STATUS = 'C'
        WHERE SEQ = #{seq}

mapper.select:
SELECT ROWNUM, HOSPITAL_SEQ ,SEQ ,(SELECT CODENAME FROM TYPECODE t  WHERE CODEID =RESERVETYPE) AS RESERVETYPE,u.NAME ,r.USER_EMAIL ,u.PHONE ,PET_NAME ,RESERVETIME ,TO_CHAR(RESERVEDATE,'YYYY-MM-DD') AS RESERVEDATE
     FROM  USERINFO u JOIN RESERVATION r 
     ON r.USER_EMAIL = u.EMAIL 
     WHERE TO_CHAR(RESERVEDATE,'YYYYMMDD')= #{reservedate}
     AND HOSPITAL_SEQ =#{hospital_seq}
     ORDER BY RESERVETIME
